REPLACE INTO `variable` (`name`, `value`) VALUES
('anonymous_validation_page_not_allowed_desciption', 's:435:"<p>In order to acquire a better feedback on the re-use of this asset, owner has limited its download to registered users only.<br />\r\n	Your feedback will be collected automatically by sending out a survey to your registered e-mail address after you have downloaded the asset.</p>\r\n<p>Please <a href="../../../user/login">log in</a> or <a href="../../../user/register">register</a> (it takes only a minute) to download this asset.</p>\r\n";'),
('og_content_type_usage_event', 's:19:"group_post_standard";');
REPLACE INTO `variable` (`name`, `value`) VALUES
('add_member_community_to_member_mail_body', 's:1491:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>You are now a member of the [title] [isa_group_type].</p>\r\n	<p>You can access this [isa_group_type] by following this link:</p>\r\n	<p>[group_url]</p>\r\n	<p>As member of this [isa_group_type], you can:</p>\r\n	<ul>\r\n		<li>\r\n			Consult all the content of the [isa_group_type]</li>\r\n		<li>\r\n			Create News items within the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/news-propose-edit-comment-and-delete-news-item">(?)</a></li>\r\n		<li>\r\n			Create documents within the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/document-create-document">(?)</a></li>\r\n		<li>\r\n			Create forum topics and discuss with users <a href="https://joinup.ec.europa.eu/contexthelp/forum-topic-create-forum-topic">(?)</a></li>\r\n		<li>\r\n			Create wiki pages within the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/wiki-create-wiki-page">(?)</a></li>\r\n		<li>\r\n			Subscribe mailing lists to communicate with them <a href="https://joinup.ec.europa.eu/contexthelp/mailing-list">(?)</a></li>\r\n		<li>\r\n			Highlight content in the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/highlighted-content">(?)</a></li>\r\n	</ul>\r\n	<p>You can consult the online help documentation to become familiar with all these functionalities:</p>\r\n	<p><a href="http://joinup.ec.europa.eu/help">http://joinup.ec.europa.eu/help</a></p>\r\n	<p>We thank you for choosing to become a member of this [isa_group_type].</p>\r\n</div>\r\n";'),
('add_member_community_to_member_mail_title', 's:52:"You are now a member of the [title] [isa_group_type]";'),
('community_create_blog_to_facilitators_mail_body', 's:331:"<p>&nbsp;</p>\r\n<div>\r\n<p>Dear [recipient-firstname],</p>\r\n<p>User [field_firstname-formatted] [field_lastname-formatted] has created a new blog post &quot;[title]&quot; in [isa_group_type] [ogname].</p>\r\n<p>To reach this blog page, click on following link : [blog_url]</p>\r\n<p>ISA-ICP Team</p>\r\n</div>\r\n<p>&nbsp;</p>\r\n<p>&nbsp;</p>";'),
('community_create_blog_to_facilitators_mail_title', 's:31:"New blog post in your community";'),
('community_create_document_to_facilitators_mail_body', 's:231:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>User [author_linked] has created a new document in the [group_url] [isa_group_type].</p>\r\n	<p>Click on the following link to access the document:</p>\r\n	<p>[isa_node_link]</p>\r\n</div>\r\n";'),
('community_create_document_to_facilitators_mail_title', 's:49:"New document created in [ogname] [isa_group_type]";'),
('community_create_news_to_facilitators_mail_body', 's:229:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>User [author_linked] has created a new news in the [group_url] [isa_group_type].</p>\r\n	<p>Click on the following link to read the news on Joinup:</p>\r\n	<p>[isa_node_link]</p>\r\n</div>";'),
('community_create_news_to_facilitators_mail_title', 's:45:"New news created in [ogname] [isa_group_type]";'),
('community_create_topic_to_facilitators_mail_body', 's:245:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>User [author_linked] has created a new forum topic in the [group_url] [isa_group_type].</p>\r\n	<p>Click on the following link to read the forum topic on Joinup:</p>\r\n	<p>[isa_node_link]</p>\r\n</div>\r\n";'),
('community_create_topic_to_facilitators_mail_title', 's:52:"New forum topic created in [ogname] [isa_group_type]";'),
('community_create_wiki_to_facilitators_mail_body', 's:241:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>User [author_linked] has created a new wiki page in the [group_url] [isa_group_type].</p>\r\n	<p>Click on the following link to read the wiki page on Joinup:</p>\r\n	<p>[isa_node_link]</p>\r\n</div>\r\n";'),
('community_create_wiki_to_facilitators_mail_title', 's:50:"New wiki page created in [ogname] [isa_group_type]";'),
('community_delete_topic_to_facilitators_mail_body', 's:158:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>User [author_linked] has deleted a forum topic &quot;[title]&quot; in [group_url] [isa_group_type].</p>\r\n</div>";'),
('community_delete_topic_to_facilitators_mail_title', 's:48:"Forum-topic deleted in [ogname] [isa_group_type]";'),
('community_join_mail_body', 's:210:"<p>Dear [field_firstname-formatted],</p>\r\n<p>You are now member of the community [title].</p>\r\n<p>As member of this community, you have now access to all contents inside this community.</p>\r\n<p>ISA-ICP Team</p>";'),
('community_join_mail_title', 's:43:"You are now member of the community [title]";'),
('community_leave_mail_body', 's:202:"<p>Dear [field_firstname-formatted],</p>\r\n<p>Your request to leave community [title] has been completed.</p>\r\n<p>From now, you are no more member of this community.</p>\r\n<p><span>ISA-ICP Team</span></p>";'),
('community_leave_mail_title', 's:32:"You leaved the community [title]";'),
('community_proposal_author_mail_body', 's:516:"<p>Dear [recipient-firstname],</p>\r\n<p>Your [isa_group_type] [group_url] has been submitted. It requires the validation from site moderator, before it will be visible on the [site-name] site.</p>\r\n<p>You will receive another message when the [isa_group_type] is accepted and published.</p>\r\n<p>In case it has been refused, you will be notified about the reasons, and eventually the possibility to submit your content again, after some updates.</p>\r\n<p>Thank you for sharing information on the [site-name] site.</p>\r\n";'),
('community_proposal_author_mail_title', 's:62:"[isa_group_type] has been submitted to moderators for approval";'),
('community_proposal_mail_body', 's:567:"<p>Dear [recipient-firstname],</p>\r\n<p>User [author_linked] wants to start a new [isa_group_type][project_in_vf].</p>\r\n<p>The user enters the following information for the [isa_group_type]:</p>\r\n<p>Title: [group_url]</p>\r\n<p>Description: [community_description]</p>\r\n<p>Domains: [community_domains]</p>\r\n<p>Languages: [community_languages]</p>\r\n<p>Kind of [isa_group_type]: [community_privacy]</p>\r\n<p>The start of the [isa_group_type] requires your validation.&nbsp;Please click on the link below to reach the validation or denial form.</p>\r\n<p>[node-edit-url]</p>\r\n";'),
('community_proposal_mail_title', 's:43:"Approval request for a new [isa_group_type]";'),
('community_proposal_vf_mail_body', 's:477:"<p>Dear [recipient-firstname],</p>\r\n<p>User [author_linked] wants to start a new [isa_group_type][project_in_vf].</p>\r\n<p>The user enters the following information for the [isa_group_type]:</p>\r\n<p>Title: [group_url]</p>\r\n<p>Description: [community_description]</p>\r\n<p>Domains: [community_domains]</p>\r\n<p>Languages: [community_languages]</p>\r\n<p>Kind of [isa_group_type]: [community_privacy]</p>\r\n<p>The start of the [isa_group_type] requires validation by a moderator.</p>\r\n";'),
('community_proposal_vf_mail_title', 's:43:"Approval request for a new [isa_group_type]";'),
('community_rejected_mail_body', 's:206:"<p>Dear [field_firstname-formatted],</p>\r\n<p>The [isa_group_type] &quot;[title]&quot; has been refused and deleted by the moderator for the following reason:</p>\r\n<p>[workflow-current-state-log-entry]</p>\r\n";'),
('community_rejected_mail_title', 's:48:"The [isa_group_type] "[title]" has been rejected";'),
('community_rejected_vf_mail_body', 's:274:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>The [isa_group_type] &quot;[title]&quot;[project_in_vf] created by [author_linked] has been refused and deleted by the moderator for the following reason:</p>\r\n	<p>[workflow-current-state-log-entry]</p>\r\n</div>\r\n<p>&nbsp;</p>\r\n";'),
('community_rejected_vf_mail_title', 's:48:"The [isa_group_type] "[title]" has been rejected";'),
('community_request_approve_to_member_mail_body', 's:1510:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>You are now a member of the [title] [isa_group_type].</p>\r\n	<p>You can access this [isa_group_type] by following this link:</p>\r\n	<p>[group_url]</p>\r\n	<p>As member of this [isa_group_type], you can:</p>\r\n	<ul>\r\n		<li>\r\n			Consult all the content of the [isa_group_type]</li>\r\n		<li>\r\n			Create News items within the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/news-propose-edit-comment-and-delete-news-item">(?)</a></li>\r\n		<li>\r\n			Create documents within the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/document-create-document">(?)</a></li>\r\n		<li>\r\n			Create forum topics and discuss with users <a href="https://joinup.ec.europa.eu/contexthelp/forum-topic-create-forum-topic">(?)</a></li>\r\n		<li>\r\n			Create wiki pages within the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/wiki-create-wiki-page">(?)</a></li>\r\n		<li>\r\n			Subscribe mailing lists to communicate with them <a href="https://joinup.ec.europa.eu/contexthelp/mailing-list">(?)</a></li>\r\n		<li>\r\n			Highlight content in the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/highlighted-content">(?)</a></li>\r\n	</ul>\r\n	<p>You can consult the online help documentation to become familiar with the functionalities of Joinup:</p>\r\n	<p><a href="http://joinup.ec.europa.eu/help">http://joinup.ec.europa.eu/help</a></p>\r\n	<p>We thank you for choosing to become a member of this [isa_group_type].</p>\r\n</div>\r\n<p>&nbsp;</p>\r\n";'),
('community_request_approve_to_member_mail_title', 's:52:"You are now a member of the [title] [isa_group_type]";'),
('community_request_deny_to_member_mail_body', 's:168:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<div>\r\n		<p>Your membership request to [isa_group_type] [group_url] has been rejected.</p>\r\n	</div>\r\n</div>\r\n<p>&nbsp;</p>\r\n";'),
('community_request_deny_to_member_mail_title', 's:56:"Membership request rejected for [isa_group_type] [title]";'),
('community_request_membership_to_facilitators_mail_body', 's:262:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>User [author_linked]&nbsp; would like to be a member of the [group_url] [isa_group_type].</p>\r\n	<p>Click on the following link to approve or reject this request:</p>\r\n	<p>[community_manage_members_url]</p>\r\n</div>\r\n";'),
('community_suspended_mail_body', 's:249:"<div>\r\n	<p>Dear [field_firstname-formatted],</p>\r\n	<p>The [isa_group_type] &quot;[group_url]&quot; has been suspended by the moderator for the following reason:</p>\r\n	<p>[workflow-current-state-log-entry]</p>\r\n</div>\r\n<div>\r\n	<p>&nbsp;</p>\r\n</div>\r\n";'),
('community_suspended_mail_title', 's:49:"The [isa_group_type] "[title]" has been suspended";'),
('community_suspended_vf_mail_body', 's:254:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>The [isa_group_type] &quot;[group_url]&quot;[project_in_vf] created by [[author_linked] has been suspended by the moderator for the following reason:</p>\r\n	<p>[workflow-current-state-log-entry]</p>\r\n</div>\r\n";'),
('community_suspended_vf_mail_title', 's:49:"The [isa_group_type] "[title]" has been suspended";'),
('community_validated_mail_title', 's:64:"Your [isa_group_type] [title] is approved to be hosted on Joinup";'),
('community_validated_vf_mail_body', 's:317:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>The&nbsp;[isa_group_type]&nbsp;&quot;[title]&quot; created by [author_linked] has been started[project_in_vf].</p>\r\n	<p>This is a [community_privacy] [isa_group_type].</p>\r\n	<p>Click on the following link to go to the [isa_group_type]:</p>\r\n	<p>[group_url]</p>\r\n</div>\r\n";'),
('community_validated_vf_mail_title', 's:47:"The [isa_group_type] "[title]" has been started";'),
('contact_page_mail_body', 's:225:"<p>Dear all,</p>\r\n<p>[contact_firsname] [contact_lastname] ([contact_page_company][contact_email]) has sent you a message on<span class="Apple-converted-space"> </span>[site-name]</p>\r\n<p>Message:</p>\r\n<p>[contact_body]</p>\r\n";'),
('contact_page_mail_title', 's:17:"[contact_subject]";'),
('contact_user_mail_body', 's:549:"<p>Dear [recipient-firstname],</p>\r\n<p>[contact_firsname] [contact_lastname] has sent you a message via your contact form ([contact_form_url]) at<span class="Apple-converted-space">&nbsp;</span>[site-name].</p>\r\n<p>If you don&#39;t want to receive such e-mails, you can change your settings at [edit_user_url]<span class="Apple-converted-space"><span class="Apple-converted-space">.</span></span></p>\r\n<p>Message:</p>\r\n<p>[contact_body]</p>\r\n<p>Do you think this message is spam? Use this form to report it: [contact_admin_url].</p>\r\n<p>&nbsp;</p>\r\n";'),
('contact_user_mail_title', 's:17:"[contact_subject]";'),
('delete_pending_revision_mail_body', 's:113:"<p>Dear [recipient-firstname],</p>\r\n<p>User [author_linked] has deleted your revision for : [isa_node_link]</p>\r\n";'),
('federated_forges_mail_body', 's:464:"<div>\r\n	<p>Dear [recipient-firstname],</p>\r\n	<p>Here is the daily report for the changes to the list of federated projects.</p>\r\n	<p>The following projects have been created:</p>\r\n	<p>!added</p>\r\n	<p>The following projects have been changed:</p>\r\n	<p>!changed</p>\r\n	<p>The following projects have been deleted:</p>\r\n	<p>!deleted</p>\r\n	<p>In case you are moderator, new projects need to be approved. They are available in the dashboard.</p>\r\n</div>\r\n<p>&nbsp;</p>\r\n";'),
('federated_forges_mail_title', 's:23:"Federated Forges report";'),
('footer_mail_body', 's:29:"<p>The [site-name] Team</p>\r\n";'),
('invite_group_mail_body', 's:858:"<p>Dear [recipient-firstname],</p>\r\n<p>User [author_linked] has invited you to join the [isa_group_type] <em>[title]</em> on [site-name].</p>\r\n<p>[invite_message]</p>\r\n<p>About the [isa_group_type] <em>[title]</em>: [group_abstract].</p>\r\n<p>To join this [isa_group_type] click on the following link [group_url]</p>\r\n<p>As a member, you will be able to participate actively in the activities of this [isa_group_type]. You will be able to create and highlight content, such as blogs, news items, events, useful software or semantic asset and participate in the discussion forums and wikis of the community. You will be automatically notified of any activities and new content.</p>\r\n<p>For a complete list of advantages of membership, click here [about_us_link].</p>\r\n<p>Do you think this message is spam? Use this form to report it: [contact_admin_url].</p>\r\n";'),
('invite_group_mail_title', 's:12:"Join [title]";'),
('invite_joinup_mail_body', 's:392:"<p>Dear future user,</p>\r\n<p>User [author_linked] ([profile_company]) wants to invite you to join [site-name].</p>\r\n<p>[invite_message]</p>\r\n<p>Follow this link to register on [site-name] :&nbsp; [invite_link]</p>\r\n<p>For a complete list of advantages of membership, click here [about_us_link].</p>\r\n<p>Do you think this message is spam? Use this form to report it: [contact_admin_url].</p>\r\n";'),
('invite_joinup_mail_title', 's:35:"Join the new collaboration platform";'),
('member_join_community_to_member_mail_body', 's:1506:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>You are now a member of the [title] [isa_group_type].</p>\r\n	<p>You can access this [isa_group_type] by following this link:</p>\r\n	<p>[group_url]</p>\r\n	<p>As member of this [isa_group_type], you can:</p>\r\n	<ul>\r\n		<li>\r\n			Consult all the content of the [isa_group_type]</li>\r\n		<li>\r\n			Create News items within the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/news-propose-edit-comment-and-delete-news-item">(?)</a></li>\r\n		<li>\r\n			Create documents within the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/document-create-document">(?)</a></li>\r\n		<li>\r\n			Create forum topics and discuss with users <a href="https://joinup.ec.europa.eu/contexthelp/forum-topic-create-forum-topic">(?)</a></li>\r\n		<li>\r\n			Create wiki pages within the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/wiki-create-wiki-page">(?)</a></li>\r\n		<li>\r\n			Subscribe mailing lists to communicate with them <a href="https://joinup.ec.europa.eu/contexthelp/mailing-list">(?)</a></li>\r\n		<li>\r\n			Highlight content in the [isa_group_type] <a href="https://joinup.ec.europa.eu/contexthelp/highlighted-content">(?)</a></li>\r\n	</ul>\r\n	<p>You can consult the online help documentation to become familiar with all these functionalities:</p>\r\n	<p><a href="http://joinup.ec.europa.eu/help">http://joinup.ec.europa.eu/help</a></p>\r\n	<p>We thank you for choosing to become a member of this [isa_group_type].</p>\r\n</div>\r\n<p>&nbsp;</p>\r\n";'),
('member_join_community_to_member_mail_title', 's:52:"You are now a member of the [title] [isa_group_type]";'),
('member_leave_community_to_member_mail_body', 's:195:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>Your request to leave [isa_group_type] [title] has been completed.</p>\r\n	<p>From now, you are no more member of this [isa_group_type].</p>\r\n</div>\r\n";'),
('member_leave_community_to_member_mail_title', 's:38:"You have left [isa_group_type] [title]";'),
('newsletters_mail_body', 's:388:"<p>\r\n<table width="100%">\r\n    <tbody>\r\n        <tr>\r\n            <td width="40%"><img src="sites/all/themes/isa_icp/images/logo/joinup.png" alt="" /></td>\r\n            <td width="60%" align="center"><font size="+3" face="Lucida Sans" color="#58a9e6"><b>[newsletter_title]</b></font></td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p>[newsletter_body]</p>\r\n<p>[newsletter_domains]</p>";'),
('newsletters_mail_footer', 's:173:"<p>&nbsp;</p>\r\n<p><a href="!confirm_unsubscribe_url">Unsubscribe from this newsletter</a></p>\r\n<p><font face="Lucida Sans" color="#58a9e6"><b>[site-name] Team</b></font></p>";'),
('news_proposal_author_mail_body', 's:509:"<p>Dear [recipient-firstname],</p>\r\n<p>Your [type] (title: [isa_node_link]) has been submitted. It requires the validation from site moderator, before it will be visible on the [site-name] site.</p>\r\n<p>You will receive another message when the [type] is accepted and published.</p>\r\n<p>In case it has been refused, you will be notified about the reasons, and eventually the possibility to submit your content again, after some updates.</p>\r\n<p>Thank you for sharing information on the [site-name] site.</p>\r\n";'),
('news_proposal_author_mail_title', 's:52:"[type] has been submitted to moderators for approval";'),
('news_proposal_moderator_mail_body', 's:291:"<p>Dear [recipient-firstname],</p>\r\n<p>User [author_linked] has proposed the following content:</p>\r\n<p>[isa_node_link]</p>\r\n<p>The publication of this content requires your validation.&nbsp;Please click on the link below to reach the validation or denial form.</p>\r\n<p>[node-edit-url]</p>\r\n";'),
('news_proposal_moderator_mail_title', 's:32:"Approval request for [type] item";'),
('news_published_mail_body', 's:217:"<p>Dear&nbsp;[recipient-firstname],</p>\r\n<div>\r\n	<p>The publishing of the [type] [isa_node_link] in the melting pot has been validated.</p>\r\n	<p>Thank you for sharing information on the [site-name] site.</p>\r\n</div>\r\n";'),
('news_published_mail_title', 's:44:"Publishing of [type] item has been validated";'),
('news_rejected_mail_body', 's:162:"<p>Dear [recipient-firstname],</p>\r\n<p>Your [type] [title] has been refused and deleted for the following reason.</p>\r\n<p>[workflow-current-state-log-entry]</p>\r\n";'),
('news_rejected_mail_title', 's:29:"[type] item has been rejected";'),
('news_requested_publication_denied_mail_body', 's:211:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>The publishing of the [type] [isa_node_link] in the melting pot has been refused for the following reason.</p>\r\n	<p>[workflow-current-state-log-entry]</p>\r\n</div>\r\n";'),
('news_requested_publication_denied_mail_title', 's:43:"Publishing of [type] item has been rejected";'),
('news_requested_publication_mail_body', 's:303:"<p>Dear [recipient-firstname],</p>\r\n<p>User [author_linked] wants to publish [type] &quot;[isa_node_link]&quot; in the melting pot.</p>\r\n<p>The publication of this content requires your validation.&nbsp;Please click on the link below to reach the validation or denial form.</p>\r\n<p>[node-edit-url]</p>\r\n";'),
('news_requested_publication_mail_title', 's:44:"Approval request publication for [type] item";'),
('news_suspended_mail_body', 's:160:"<p>Dear [recipient-firstname],</p>\r\n<p>Your [type] [isa_node_link] has been suspended for the following reason.</p>\r\n<p>[workflow-current-state-log-entry]</p>\r\n";'),
('news_suspended_mail_title', 's:30:"[type] item has been suspended";'),
('news_validated_mail_body', 's:260:"<p>Dear&nbsp;[recipient-firstname],</p>\r\n<p>Your [type] [isa_node_link] has been accepted and published.</p>\r\n<p>It is now visible for any registered or not users on the [site-name] site.</p>\r\n<p>Thank you for sharing information on the [site-name] site.</p>\r\n";'),
('news_validated_mail_title', 's:30:"[type] item has been published";'),
('new_pending_revision_mail_body', 's:295:"<p>Dear [recipient-firstname],</p>\r\n<p>User [author_linked] has updated the following content:</p>\r\n<p>[isa_node_link]</p>\r\n<p>The publication of this content requires your validation.&nbsp;Please click on the link below to reach the validation or denial form.</p>\r\n<p>[node_revisions_url]</p>\r\n";'),
('prefix_subject_mail_title', 's:8:"Joinup: ";'),
('project_surveys_mail_body', 's:336:"<p>Dear [recipient-firstname]<br />\r\n	<br />\r\n	Thanks for downloading the project [dl_project_title].<br />\r\n	<br />\r\n	In order to improve the quality of our product we would like to have your feedback on our projects. The link below will redirect you to an online survey.<br />\r\n	<br />\r\n	[survey_url]<br />\r\n	<br />\r\n	Thank you,</p>\r\n";'),
('project_surveys_mail_title', 's:34:"[dl_project_title] download survey";'),
('release_approved_mail_body', 's:207:"<p>Dear&nbsp;[recipient-firstname],</p>\r\n<div>\r\n	<p>Your request assessmentfor the release [isa_node_link] has been approved.</p>\r\n	<p>Thank you for sharing information on the [site-name] site.</p>\r\n</div>\r\n";'),
('release_approved_mail_title', 's:56:"Request assessment for release [title] has been approved";'),
('release_rejected_mail_body', 's:218:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>Your request assessment for the release [isa_node_link] has been refused for the following reason:</p>\r\n	<p>[workflow-current-state-log-entry]</p>\r\n</div>\r\n<p>&nbsp;</p>\r\n";'),
('release_rejected_mail_title', 's:56:"Request assessment for release [title] has been rejected";'),
('release_requested_to_author_mail_body', 's:283:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>Your request assessment on the release [isa_node_link] has been submitted. It requires the validation by clearing process manager</p>\r\n	<p>You will receive another message for inform if this request is accepted or refused.</p>\r\n</div>\r\n";'),
('release_requested_to_author_mail_title', 's:78:"Request assessment has been submitted to clearing process manager for approval";'),
('release_requested_to_cp_manager_mail_body', 's:302:"<p>Dear [recipient-firstname],</p>\r\n<p>User [author_linked] has sent a request assessment for the following release:</p>\r\n<div>\r\n	<p>[isa_node_link]</p>\r\n	<p>This request requires your validation.&nbsp;Please click on the link below to reach the approvation or denial form.</p>\r\n</div>\r\n<p>&nbsp;</p>\r\n";'),
('release_requested_to_cp_manager_mail_title', 's:39:"Approval request for request assessment";'),
('remove_member_community_to_member_mail_body', 's:122:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>Your membership on [isa_group_type] [title] has been removed.</p>\r\n</div>\r\n";'),
('remove_member_community_to_member_mail_title', 's:60:"Your membership on [isa_group_type] [title] has been removed";'),
('user_change_password_mail_body', 's:129:"<p>Dear [recipient-firstname],</p>\r\n<p>Your password has been correctly changed. You can use it for log in on [site-name] .</p>\r\n";'),
('user_change_password_mail_title', 's:30:"Your password has been changed";'),
('user_mail_password_reset_body', 's:436:"Dear [recipient-firstname],\r\n\r\nA request to reset the password for your account has been made at !site.\r\n\r\nYou may now log in to !uri_brief by clicking on this link or copying and pasting it in your browser:\r\n\r\n!login_url\r\n\r\nThis is a one-time login, so it can be used only once. It expires after one day and nothing will happen if it''s not used.\r\n\r\nAfter logging in, you will be redirected to !edit_uri so you can change your password.";'),
('user_mail_password_reset_subject', 's:52:"Replacement login information for !username at !site";'),
('user_mail_register_admin_created_body', 's:468:"!username,\r\n\r\nA site administrator at !site has created an account for you. You may now log in to !login_uri using the following username and password:\r\n\r\nusername: !username\r\npassword: !password\r\n\r\nYou may also log in by clicking on this link or copying and pasting it in your browser:\r\n\r\n!login_url\r\n\r\nThis is a one-time login, so it can be used only once.\r\n\r\nAfter logging in, you will be redirected to !edit_uri so you can change your password.\r\n\r\n\r\n--  !site team";'),
('user_mail_register_admin_created_subject', 's:52:"An administrator created an account for you at !site";'),
('user_mail_register_no_approval_required_body', 's:339:"Dear [recipient-firstname],\r\n\r\nThank you for registering Joinup platform.\r\n\r\nYou must confirm your registration by clicking on this link or copying and pasting it in your browser:\r\n\r\n!login_url\r\n\r\nThis is a one-time login, so it can be used only once.\r\n\r\nAfter logging in, you will be redirected to !edit_uri you must change your password.";'),
('user_mail_register_no_approval_required_subject', 's:29:"Account details for !username";'),
('user_mail_register_pending_approval_body', 's:273:"!username,\r\n\r\nThank you for registering at !site. Your application for an account is currently pending approval. Once it has been approved, you will receive another e-mail containing information about how to log in, set your password, and other details.\r\n\r\n\r\n--  !site team";'),
('user_mail_register_pending_approval_subject', 's:63:"Account details for !username at !site (pending admin approval)";'),
('user_mail_status_activated_body', 's:122:"Dear [recipient-firstname],\r\n\r\nYour account has been reactivated. You are now able to connect again to the !site platform.";'),
('user_mail_status_activated_notify', 'i:1;'),
('user_mail_status_activated_subject', 's:40:"Account activated for !username at !site";'),
('user_mail_status_blocked_body', 's:126:"Dear [recipient-firstname],\r\n\r\nYour account has been blocked. In case of error, you can contact us thanks to the contact form.";'),
('user_mail_status_blocked_notify', 'i:1;'),
('user_mail_status_blocked_subject', 's:48:"Account details for !username at !site (blocked)";'),
('user_mail_status_deleted_body', 's:53:"!username,\r\n\r\nYour account on !site has been deleted.";'),
('user_mail_status_deleted_notify', 'i:0;'),
('user_mail_status_deleted_subject', 's:48:"Account details for !username at !site (deleted)";'),
('subscription_introduction_text', 's:150:"<p>On Joinup, you can subscribe to content (e.g. a wiki page) and groups (e.g. a community) and receive notifications via e-mail of any changes.</p>\r\n";'),
('community_create_event_to_facilitators_mail_body', 's:225:"<p>Dear [recipient-firstname],</p>\r\n<div>\r\n	<p>User [author_linked] has created a new event in the [group_url] [isa_group_type].</p>\r\n	<p>Click on the following link to access the event:</p>\r\n	<p>[isa_node_link]</p>\r\n</div>\r\n";'),
('community_create_event_to_facilitators_mail_title', 's:46:"New event created in [ogname] [isa_group_type]";'),
