  `writein` int(11) NOT NULL DEFAULT '0',
  PRIMARY KEY (`cid`),
  KEY `nid` (`nid`,`weight`)
) ENGINE=InnoDB AUTO_INCREMENT=9 DEFAULT CHARSET=utf8;

